import React, { useState, useEffect } from "react";
import "./Popup.css";
import TextAreaForm from "./Form";

const Popup = () => {
  const [isDragging, setIsDragging] = useState(false);
  const [dragStartPos, setDragStartPos] = useState({ x: 0, y: 0 });
  const [distanceMoved, setDistanceMoved] = useState({ x: 0, y: 0 });

  const handleDragStart = (event: React.MouseEvent) => {
    setIsDragging(true);
    setDragStartPos({ x: event.clientX, y: event.clientY });
    setDistanceMoved({ x: 0, y: 0 });
  };

  useEffect(() => {
    const handleMouseMove = (event: MouseEvent) => {
      if (isDragging) {
        const div = document.getElementById("draggedDiv");
        if (div) {
          const offsetX = event.clientX - dragStartPos.x + distanceMoved.x;
          const offsetY = event.clientY - dragStartPos.y + distanceMoved.y;
          div.style.left = offsetX + "px";
          div.style.top = offsetY + "px";
        }
      }
    };

    const handleMouseUp = () => {
      if (isDragging) {
        setIsDragging(false);
        setDistanceMoved((prevDistance) => ({
          x: prevDistance.x + event.clientX - dragStartPos.x,
          y: prevDistance.y + event.clientY - dragStartPos.y,
        }));
      }
    };

    document.addEventListener("mousemove", handleMouseMove);
    document.addEventListener("mouseup", handleMouseUp);

    return () => {
      document.removeEventListener("mousemove", handleMouseMove);
      document.removeEventListener("mouseup", handleMouseUp);
    };
  }, [isDragging, dragStartPos, distanceMoved]);

  return (
    <div
      id="draggedDiv"
      className={isDragging ? "dragging l-popup" : "l-popup"}
      style={{
        position: "fixed",
        top: "0",
        left: "0",
        width: "400px",
        height: "300px",
        userSelect: isDragging ? "none" : "all",
        cursor: isDragging ? "grabbing" : "grab",
        zIndex: "500",
      }}
    >
      <div style={{ padding: "16px" }}>
        <div
          style={{
            position: "relative",
            width: "100%",
            height: "100%",
          }}
        >
          {isDragging ? <p>クリック中</p> : <p>クリックされていません</p>}
          <TextAreaForm />
        </div>
      </div>
      <div
        style={{
          position: "absolute",
          top: "0",
          width: "400px",
          height: "300px",
          userSelect: isDragging ? "none" : "all",
          cursor: isDragging ? "grabbing" : "grab",
          zIndex: "600",
        }}
        onMouseDown={handleDragStart}
      ></div>
    </div>
  );
};

export default Popup;

