import React, { useState, useEffect, useRef } from "react";
import "./Popup.css";

const Popup = () => {
  const [position, setPosition] = useState({ x: 0, y: 0 });
  const [isDragging, setIsDragging] = useState(false);
  const [dragOffset, setDragOffset] = useState({ x: 0, y: 0 });
  const containerRef = useRef<HTMLDivElement>(null);

  const handleDragStart = (event: React.MouseEvent) => {
    setIsDragging(true);
    const { clientX, clientY } = event;
    const offsetX = clientX - position.x;
    const offsetY = clientY - position.y;
    setDragOffset({ x: offsetX, y: offsetY });
  };

  const handleDrag = (event: React.MouseEvent) => {
    if (!isDragging) return;
    const { clientX, clientY } = event;
    const newX = clientX - dragOffset.x;
    const newY = clientY - dragOffset.y;
    setPosition({ x: newX, y: newY });
  };

  const handleDragEnd = () => {
    setIsDragging(false);
  };

  const [inputText, setInputText] = useState("");

  const handleChange = (event: React.ChangeEvent) => {
    setInputText("a");
  };

  const handleRelease = () => {
    handleDragEnd(); // handleDragEnd を呼び出す
  };
  useEffect(() => {
    const handleGlobalMouseUp = (event: MouseEvent) => {
      // containerRef 以外の要素でマウスボタンが解除された場合に handleRelease を実行
      if (
        containerRef.current &&
        !containerRef.current.contains(event.target as Node)
      ) {
        handleRelease();
      }
    };

    document.addEventListener("mouseup", handleGlobalMouseUp);

    return () => {
      document.removeEventListener("mouseup", handleGlobalMouseUp);
    };
  }, []);

  return (
    <div
      className="l-popup"
      style={{
        position: "fixed",
        top: position.y,
        left: position.x,
        width: "300px",
        height: "200px",
        userSelect: isDragging ? "none" : "all",
        cursor: isDragging ? "grabbing" : "grab",
      }}
      onMouseDown={handleDragStart}
      onMouseMove={handleDrag}
      onMouseUp={handleRelease}
    >
      <h1>Hello!</h1>
      {isDragging ? <p>クリック中</p> : <p>クリックされていません</p>}
      <form>
        <input type="text" value={inputText} onChange={handleChange} />
        <input type="submit" value="Add Memo" />
      </form>
    </div>
  );
};

export default Popup;
