import React, { useState, useRef, useEffect } from "react";
import Popup from "./Popup";

const TextAreaForm = () => {
  const [text, setText] = useState("");
  const [inputText, setInputText] = useState("");
  const [savedLines, setSavedLines] = useState<string[]>([]);

  const textareaRef = useRef<HTMLTextAreaElement>(null);
  const divRef = useRef<HTMLDivElement>(null);

  const handleChange = (event: React.ChangeEvent<HTMLTextAreaElement>) => {
    setText(event.target.value);
  };

  const handleSave = () => {
    const lines = text.split("\n");
    setSavedLines(lines);
  };

  const handleLineClick = (line: string) => {
    if (inputText === line) {
      // 送信処理
      console.log(line);
      setInputText("");
    } else {
      setInputText(line);
    }
  };

  const handleInputChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setInputText(event.target.value);
  };

  const [divHeight, setDivHeight] = useState<number | null>(null);

  useEffect(() => {
    if (divRef.current) {
      const divElement = divRef.current;
      const { height } = divElement.getBoundingClientRect();
      setDivHeight(height - 300);
    }
  }, []);

  return (
    <div style={{ position: "relative", zIndex: "1000" }}>
      <textarea
        ref={textareaRef}
        value={text}
        onChange={handleChange}
        rows={5}
        cols={30}
        style={{
          width: "100%",
          height: "80px",
          resize: "none",
          boxSizing: "border-box",
        }}
      />
      <button onClick={handleSave}>保存</button>
      <input
        type="text"
        value={inputText}
        onChange={handleInputChange}
        style={{
          width: "100%",
          backgroundColor: "white",
          overflowX: "hidden",
          overflowY: "scroll",
          boxSizing: "border-box",
        }}
      />
      <div
        ref={divRef}
        style={{
          width: "100%",
          height: `${changeHeight}px`,
          backgroundColor: "white",
          overflowX: "hidden",
          overflowY: "scroll",
          cursor: "default",
        }}
      >
        {savedLines.map((line, index) => (
          <p key={index} onClick={() => handleLineClick(line)}>
            {line}
          </p>
        ))}
      </div>
    </div>
  );
};

export default TextAreaForm;
